 var slideIndex = 0;
 showSlides();
 
 function showSlides() {
   var i;
   var slides = document.getElementsByClassName("mySlides");
   for (i = 0; i < slides.length; i++) {
     slides[i].style.display = "none";
   }
   slideIndex++;
   if (slideIndex > slides.length) { slideIndex = 1 }
   slides[slideIndex - 1].style.display = "block";
   setTimeout(showSlides, 7000); // Change image every 7 seconds
 }

  //Navigation Bar Menu
  $(".f-nav-toggler").click(function() {
    $(".f-toggler-content").toggle(500);
  });

  //Block devices with 900 pixels wide
  window.deviceCheck = function() {
    var check = false;
    if (window.innerWidth > 800) {
      check = true;
    }
    return check;
  }

  if (window.deviceCheck()) {
    document.getElementsByTagName("body")[0].style.display = "none";
    alert("Sorry but the current page is not available on desktop or devices higher than 800 pixels.\nPlease comeback later!");
  }

  $('#fileType').on('change', function() {
    switch ($('#fileType option:selected').val()) {
      case '1':
        $(".epub").css("display", "block");
        $(".pdf").css("display", "none");
        break;
      case '2':
        $(".epub").css("display", "none");
        $(".pdf").css("display", "block");
        break;
      default:
    }
  });
  
